import React from "react";
import { v4 as uuidv4 } from "uuid";
import PokemonSelect from "./PokemonSelect";
import PokemonCard from "./PokemonCard";
import "./PokeDex.css";
import useAxios from "./hooks/useAxios"; 

function PokeDex() {
  const [pokemon, addPokemon] = useAxios(
    "https://pokeapi.co/api/v2/pokemon/",
    responseData => ({
      id: uuidv4(),
      front: responseData.sprites.front_default,
      back: responseData.sprites.back_default,
      name: responseData.name,
      stats: responseData.stats.map(stat => ({
        name: stat.stat.name,
        value: stat.base_stat,
      })),
    })
  );

  return (
    <div className="PokeDex">
      <div className="PokeDex-buttons">
        <h3>Please select your pokemon:</h3>
        <PokemonSelect add={addPokemon} />
      </div>
      <div className="PokeDex-card-area">
        {pokemon.map(cardData => (
          <PokemonCard
            key={cardData.id}
            front={cardData.sprites.front_default}
            back={cardData.sprites.back_default}
            name={cardData.name}
            stats={cardData.stats.map (stat => ({value: stat.base_stat, name: stat.stat.name}))}
          />
        ))}
      </div>
    </div>
  );
}


export default PokeDex;




